# Write your MySQL query statement below
Select p.product_id,round(ifnull(sum(u.units*p.price)/(sum(u.units)),0),2) as average_price from UnitsSold u right  join Prices p on p.product_id=u.product_id where (u.purchase_date between p.start_date and p.end_date) or (u.purchase_date is null) group by p.product_id;